INSERT INTO templates (`id`, `description`, `template_name`, `template_text`) VALUES (unhex("0848bfb0b3ed48cbb069d88998dff22a"), 'Заявление на матпомощь', 'maternity_help', '<html>\n<head>\n    <meta charset=\"UTF-8\" />\n    <title>{{template_name}}</title>\n</head>\n<style>\n    body {\n        /*width: 100%;*/\n        /*height: 100%;*/\n        /*margin: 0;*/\n        /*padding: 0;*/\n        /*background-color: #FAFAFA;*/\n        font-family: "PT Sans", sans-serif;\n    }\n\n    /** {*/\n        /*box-sizing: border-box;*/\n        /*-moz-box-sizing: border-box;*/\n    /*}*/\n\n    /*.page {*/\n        /*width: 210mm;*/\n        /*min-height: 297mm;*/\n        /*padding: 20mm;*/\n        /*margin: 10mm auto;*/\n        /*border: 1px #D3D3D3 solid;*/\n        /*border-radius: 5px;*/\n        /*background: white;*/\n        /*box-shadow: 0 0 5px rgba(0, 0, 0, 0.1);*/\n    /*}*/\n\n    @page {\n        size: A4;\n    }\n\n    /*@media print {*/\n        /*html, body {*/\n            /*width: 210mm;*/\n            /*height: 297mm;*/\n        /*}*/\n\n        /*.page {*/\n            /*margin: 0;*/\n            /*border: initial;*/\n            /*border-radius: initial;*/\n            /*width: initial;*/\n            /*min-height: initial;*/\n            /*box-shadow: initial;*/\n            /*background: initial;*/\n            /*page-break-after: always;*/\n        /*}*/\n    /*}*/\n\n    .hat {\n        display: inline-block;\n        width: 100%;\n    }\n\n    .hat > #hat-content {\n        float: right;\n        width: 60mm;\n    }\n\n    .header {\n        margin: 6mm auto;\n    }\n\n    .header > #header-content {\n        margin: 0 auto;\n        text-align: center;\n    }\n\n    .content {\n        margin-bottom: 4mm;\n    }\n\n    .signature-container {\n        display: inline-block;\n        width: 100%;\n        margin-bottom: 6mm;\n    }\n\n    .signature-date {\n        float: left;\n    }\n\n    .signature-itself {\n        float: right;\n    }\n</style>\n<body class=\"page\">\n<div class=\"hat\">\n    <div id=\"hat-content\">\n        Директору школы ФРКТ <br />\n        Дворковичу А.В. <br />\n        {{#is_male}} от студента {{/is_male}} {{^is_male}} от студентки {{/is_male}}\n        {{group}} группы <br />\n        {{surname}} {{name}} <br />\n    </div>\n</div>\n<div class=\"header\">\n    <div id=\"header-content\">ЗАЯВЛЕНИЕ</div>\n</div>\n<div class=\"content\">\n    Прошу оказать мне материальную помощь в связи с тяжёлым материальным положением. <br />\n    Причина: {{reason}}\n</div>\n<div class=\"footer\">\n    <div class=\"signature-container\">\n        <div class=\"signature-date\">{{date}}</div>\n        <div class=\"signature-itself\">_________________ / {{surname}} {{name}}</div>\n    </div>\n    <div class=\"signature-container\">\n        Решение стипендиальной комиссии: материальную помощь назначить / не назначать <br />\n        <div class=\"signature-date\">«____» _____________ ____г.</div>\n        <div class=\"signature-itself\">_________________ / Микоян Ф.А.</div>\n    </div>\n</div>\n</body>\n</html>');